// Download Link
function dl(page) {
    var destination = page;
    setTimeout(function(){window.location=page},4000);
	return true;
};

function subscribe() {
	var customerEmail = document.getElementById('newsletteremail').value;
	analytics.identify(customerEmail.toString(),{
					email: customerEmail.toString(),
					newsletter: true
				});
	analytics.track('Subscribe Newsletter');
};

function downloadIndustryReport() {
	var customerEmail = '';
	var newslettercheck = '';
	var customerFirstName = '';
	var customerLastName = '';
	var customerCompany = '';
	var customerJob = '';

	customerEmail = document.getElementById('newsletteremail').value;
	newslettercheck = document.getElementsByName('choice-newsletter[]')[0].checked;
	customerFirstName = document.getElementById('customerfirstname').value; 
	customerLastName = document.getElementById('customerlastname').value; 
	customerCompany = document.getElementById('customercompany').value; 
	customerJob = document.getElementById('customerjob').value; 

	if (newslettercheck !== false){
	analytics.identify(customerEmail.toString(),{
					email: customerEmail.toString(),
					firstName: customerFirstName.toString(),
					lastName: customerLastName.toString(),
					title: customerJob.toString(),
					company: customerCompany.toString(),
					newsletter: true
				});
	}
	analytics.track('Download AR Report');
};